// ===========================================
.sites-section {
  margin-bottom: 160px;

  &__header {
    text-align: center;
    margin-bottom: 64px;
  }

  &__subtitle {
    font-family: "Montserrat", sans-serif;
    font-weight: 400;
    font-size: 32px;
    line-height: 1.4;
    color: $color-accent;
    margin: 0 0 8px;
  }

  &__title {
    font-family: "Lora", serif;
    font-weight: 500;
    font-size: 64px;
    line-height: normal;
    color: $color-dark;
    margin: 0;
  }

  &__content {
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 16px;
  }
}

// Карточка с картой мира
.map-card {
  position: relative;
  width: 100%;
  height: 300px;
  background: $color-primary;
  border-radius: 32px;
  overflow: hidden;

  &__map {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, calc(-50% - 74.5px));
    width: 1172px;
    height: 659px;
    object-fit: cover;
    pointer-events: none;
  }

  &__clouds {
    position: absolute;
    left: -416px;
    top: 0;
    width: 1920px;
    height: 1080px;
    object-fit: cover;
    pointer-events: none;
  }

  &__gradient {
    position: absolute;
    left: 0;
    top: 0;
    width: 800px;
    height: 100%;
    background: linear-gradient(
      90deg,
      rgba(36, 39, 48, 0.6) 0%,
      rgba(36, 39, 48, 0) 100%
    );
    mix-blend-mode: multiply;
  }

  &__stats {
    position: absolute;
    left: 61px;
    top: 76px;
    display: flex;
    gap: 61px;
    z-index: 2;
  }

  &__stat {
    &-value {
      font-family: "Lora", serif;
      font-weight: 500;
      font-size: 64px;
      line-height: normal;
      color: white;
      margin: 0 0 6px;

      sup {
        font-size: 41.28px;
        vertical-align: super;
      }
    }

    &-label {
      font-family: "Montserrat", sans-serif;
      font-weight: 400;
      font-size: 24px;
      line-height: normal;
      color: white;
      margin: 0;
      text-indent: 0 !important;
    }
  }
}

// Переключатель стран
.country-switcher {
  position: absolute;
  left: 50%;
  top: 286px;
  transform: translateX(-50%);
  display: flex;
  gap: 4px;
  padding: 4px;
  background: $color-border;
  border: 8px solid white;
  border-radius: 16px;
  z-index: 10;

  &__item {
    display: flex;
    align-items: center;
    gap: 8px;
    width: 131px;
    height: 36px;
    padding: 0 16px 0 22px;
    background: transparent;
    border: none;
    border-radius: 32px;
    cursor: pointer;
    transition: all 0.2s;

    &--active {
      background: rgba(255, 255, 255, 0.9);
      border-radius: 12px;

      .country-switcher__name {
        color: #000;
      }
    }
  }

  &__flag {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    overflow: hidden;
    flex-shrink: 0;

    img {
      width: 100%;
      height: 100%;
      object-fit: cover;
    }
  }

  &__name {
    font-family: "Montserrat", sans-serif;
    font-weight: 400;
    font-size: 20px;
    line-height: 1.2;
    color: rgba(0, 0, 0, 0.6);
    transition: color 0.2s;
  }
}

// Фото производства
.production-photo {
  width: 100%;
  height: 420px;
  border-radius: 32px;
  overflow: hidden;

  &__image {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
}

// ===========================================
// FULL CYCLE PRODUCTION
// ===========================================
.full-cycle {
  display: flex;
  gap: 32px;
  margin-bottom: 160px;

  &__text {
    flex: 0 0 352px;
    padding-top: 62px;
  }

  &__title {
    font-family: "Lora", serif;
    font-weight: 500;
    font-size: 48px;
    line-height: normal;
    color: #000;
    margin: 0 0 24px;
  }

  &__description {
    font-family: "Montserrat", sans-serif;
    font-weight: 400;
    font-size: 24px;
    line-height: 1.4;
    color: $color-dark;
    margin: 0 0 48px;
  }

  &__highlight {
    font-style: italic;
    color: $color-accent;
  }

  &__certifications {
    display: flex;
    align-items: center;
    gap: 24px;
  }

  &__cert-logo {
    opacity: 0.1;

    &--ce {
      width: 106px;
      height: 75px;
    }

    &--iso {
      width: 120px;
      height: 100px;
    }
  }

  &__card {
    flex: 1;
    display: flex;
    flex-direction: column;
    gap: 8px;
    background: $color-light-bg;
    border-radius: 32px;
    overflow: hidden;
  }
}

// Блок с этапами производства
.production-steps {
  position: relative;
  height: 376px;
  background: white;
  border-radius: 24px;
  overflow: hidden;
  margin: 8px 8px 0;

  &__bg {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    pointer-events: none;
  }

  &__bg-overlay {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    pointer-events: none;
    z-index: 1;
  }

  &__content {
    position: relative;
    z-index: 2;
    padding: 64px 69px;
    display: flex;
    flex-direction: column;
    gap: 8px;
  }

  &__row {
    display: flex;
    align-items: center;
    gap: 8px;

    &--1 {
      margin-left: 38px;
    }

    &--2 {
      margin-left: 0;
    }

    &--3 {
      margin-left: 118px;
    }

    &--4 {
      margin-left: 142px;
    }
  }

  &__step {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    padding: 14px 32px 13px;
    border-radius: 32px;
    font-family: "Montserrat", sans-serif;
    font-weight: 400;
    font-size: 24px;
    line-height: 1.2;
    text-transform: uppercase;
    letter-spacing: 2.4px;
    white-space: nowrap;

    &--dark {
      background: url("/images/production-sites/Nero Marquina 1.png") center /
        cover;
      color: white;
    }

    &--light {
      background: white;
      color: #000;
    }

    &--accent {
      background: $color-primary;
      color: white;
    }
  }

  &__arrow {
    flex-shrink: 0;

    img {
      display: block;
      height: 56px;
      width: auto;
    }

    &--small img {
      width: 81px;
    }

    &--reverse {
      img {
        width: 81px;
      }
    }
  }

  &__down-arrow {
    position: absolute;
    left: 50%;
    bottom: -60px;
    transform: translateX(-50%) rotate(90deg);
    z-index: 3;

    img {
      width: 56px;
      height: 120px;
    }
  }
}

// Блок с результатом
.excellent-result {
  position: relative;
  height: 222px;
  background: white;
  border-radius: 24px;
  overflow: hidden;
  margin: 0 8px 8px;

  &__overlay {
    position: absolute;
    left: 0;
    top: 0;
    z-index: 2;

    svg {
      display: block;
    }
  }

  &__text {
    position: absolute;
    left: 16px;
    top: 16px;
    font-family: "Lora", serif;
    font-weight: 500;
    font-size: 32px;
    line-height: normal;
    color: $color-accent;
    margin: 0;
    z-index: 3;
  }

  &__image {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    pointer-events: none;
  }
}

// ===========================================
// DOOR-TO-DOOR - Схема доставки
// ===========================================
.door-to-door {
  margin-bottom: 32px;

  &__title {
    font-family: "Lora", serif;
    font-weight: 500;
    font-size: 48px;
    line-height: normal;
    color: #000;
    margin: 0 0 32px;
  }

  &__scheme {
    position: relative;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    height: 290px;
    padding-top: 50px;
  }

  // Точки на схеме
  &__point {
    position: absolute;
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 5;

    &--start {
      left: -20px;
      top: calc(50% + 72px);
    }

    &--mid1 {
      left: calc(33.33% - 20px);
      top: calc(50% + 72px);
    }

    &--mid2 {
      left: calc(66.67% - 20px);
      top: calc(50% + 73px);
    }
  }

  &__diamond {
    width: 8px;
    height: 8px;
    background: white;
    transform: rotate(45deg);
  }

  // Сегменты схемы
  &__segment {
    position: relative;

    &--ship {
      padding-right: 20px;
    }

    &--auto {
      padding-left: 20px;
    }
  }

  // Линии SVG
  &__line {
    position: absolute;

    &--ship {
      left: 0;
      top: 320px;
      width: 100%;
      height: 164px;
    }

    &--train-top {
      left: 0;
      top: 0;
      width: 100%;
      height: 169px;
    }

    &--train-mid {
      left: 0;
      top: 80px;
      width: 100%;
      height: 89px;
    }

    &--train-bottom {
      left: 0;
      top: 161px;
      width: 100%;
      height: 8px;
    }
  }

  // Контейнеры справа
  &__containers {
    position: absolute;
    right: 0;
    top: 0;
    display: flex;
    flex-direction: column;
    gap: 16px;
  }

  &__container-line {
    position: relative;
    width: 373px;
    height: 64px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 32px;
    display: flex;
    align-items: center;

    &::before {
      content: "";
      position: absolute;
      left: 0;
      top: 50%;
      transform: translateY(-50%);
      width: calc(100% - 50px);
      height: 2px;
      background: $color-primary;
      border-radius: 1px;
    }
  }

  &__container-point {
    position: absolute;
    left: -20px;
    top: 50%;
    transform: translateY(-50%);
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;

    &::after {
      content: "";
      width: 8px;
      height: 8px;
      background: white;
      transform: rotate(45deg);
    }
  }

  // Подписи
  &__label {
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    font-family: "Lora", serif;
    font-weight: 500;
    font-size: 32px;
    line-height: normal;
    color: #b9b9b9;
    text-align: center;
    margin: 0;
  }

  // Разделители
  &__divider {
    position: absolute;
    top: calc(100% - 32px);
    width: 2px;
    height: 26px;
    background: #b9b9b9;
    border-radius: 1px;

    &--1 {
      left: calc(33.33% - 1px);
    }

    &--2 {
      left: calc(66.67% - 1px);
    }
  }
}

// ===========================================
// BENEFITS ROW - Карточки преимуществ
// ===========================================
.benefits-row {
  position: relative;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  border-radius: 32px;
  overflow: hidden;
  background: linear-gradient(90deg, $color-light-bg 0%, $color-light-bg 100%);

  &__divider {
    position: absolute;
    left: calc(66.67% + 0.33px);
    top: 0;
    bottom: 0;
    width: 2px;
    background: linear-gradient(
      180deg,
      transparent 0%,
      rgba(0, 0, 0, 0.08) 20%,
      rgba(0, 0, 0, 0.08) 80%,
      transparent 100%
    );
  }
}

.benefit-card {
  position: relative;
  padding: 32px;
  min-height: 269px;

  &--dark {
    background: $color-dark;

    .benefit-card__icon {
      background: white;
    }

    .benefit-card__title,
    .benefit-card__description {
      color: white;
    }
  }

  &--light {
    background: rgba(240, 240, 240, 1);

    .benefit-card__icon {
      background: #e9e9ea;
    }

    .benefit-card__title,
    .benefit-card__description {
      color: #000;
    }
  }

  &__icon {
    width: 64px;
    height: 64px;
    border-radius: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 20px;
  }

  &__title {
    font-family: "Lora", serif;
    font-weight: 500;
    font-size: 32px;
    line-height: normal;
    margin: 0 0 16px;
  }

  &__description {
    font-family: "Montserrat", sans-serif;
    font-weight: 400;
    font-style: italic;
    font-size: 20px;
    line-height: 1.3;
    margin: 0;
    max-width: 315px;
  }
}

// ===========================================
// АДАПТИВНОСТЬ
// ===========================================
