  .production-sites__container {
    max-width: 100%;
    padding: 0 16px;
  }

  .map-card__stats {
    left: 32px;
  }

  .full-cycle {
    &__text {
      flex: 0 0 300px;
    }
  }

  .production-steps__content {
    padding: 40px;
  }
}

