    }
  }

  .production-steps {
    height: auto;
    min-height: 350px;

    &__content {
      padding: 32px 24px;
    }

    &__step {
      font-size: 16px;
      padding: 10px 20px;
    }

    &__row {
      margin-left: 0 !important;
      flex-wrap: wrap;
    }
  }

  .door-to-door {
    &__title {
      font-size: 40px;
    }

    &__scheme {
      display: none; // Схема сложная, скрываем на мобильных
    }
  }

  .benefits-row {
    grid-template-columns: 1fr;
  }

  .benefit-card {
    min-height: auto;
    padding: 24px;

    &--dark {
      border-radius: 32px 32px 0 0;
    }
  }
}

