    max-width: none;
    border-width: 4px;
  }

  .production-photo {
    height: 250px;
    margin-top: 0;
  }

  .full-cycle {
    margin-bottom: 80px;

    &__title {
      font-size: 32px;
    }

    &__description {
      font-size: 18px;
    }

    &__certifications {
      gap: 16px;
    }

    &__cert-logo {
